    this.pc_onRemoteStreamAdded = function(){
    	var curObj = this;
    	return function(e){
    		var remoteStreamURI = webkitURL.createObjectURL(e.stream);
    		curObj.owner.onRemoteStream(remoteStreamURI);
    	};
    };

